#==============================================================================

def reverse_complement(strand : str) -> str:
    alphabet = { 'A' : 't',
                 'C' : 'g',
                 'G' : 'c',
                 'T' : 'a' }
    strand = strand.upper()
    for base, complement in alphabet.items():
        strand = strand.replace(base, complement)
    strand = strand.upper()[::-1]
    return strand

#==============================================================================

if __name__ == '__main__':
    strand = r''
    complement = reverse_complement(strand)
    
