#==============================================================================

def pattern_matching(pattern : str, genome : str) -> list:
    pattern = pattern.upper()
    genome = genome.upper()
    starts = []
    
    i = 0
    while i < len(genome) - 1:
        try:
            start = genome[i:].index(pattern) + i
            starts.append(start)
            i = start + 1
        except ValueError:
            i = len(genome)
    return ' '.join(str(s) for s in starts)

#==============================================================================

if __name__ == '__main__':
    pattern = 'GTGCATAGT'
    genome = ''
    starts = pattern_matching(pattern, genome)
    
